<?php// src/Controller/BlogController.phpnamespace App\Controller;use Symfony\Bundle\FrameworkBundle\Controller\AbstractController;use Symfony\Component\HttpFoundation\Response;use Symfony\Component\Routing\Annotation\Route;class BlogController extends AbstractController{    /**     * @Route("/blog", name="blog_index")     */    public function index()    {        return $this->render('index.html.twig', [            'owner' => 'Thomas',        ]);    }    /**     * @Route("/blog/list/{page}", name="blog_list", requirements={"page"="\d+"})     */    public function list($page)    {        return $this->render('list.html.twig', ['page' => $page]);    }    /**     * @Route("/blog/show/{slug}", defaults={"slug"="article-sans-titre"}, requirements={"slug"="[a-z0-9-]*"}, name="blog_show")     */    public function show(string $slug)    {        $slug = ucwords(str_replace('-', ' ', $slug));        return $this->render('show.html.twig', ['slug' => $slug]);    }}